<?php $account = $this->db->get_where('user', array('id' => $user_id))->row_array(); ?>        <div class="row">          <div class="col-md-2"></div>          <div class="col-md-8">            <div class="card">              <div class="card-header">                <h5 class="title">Edit Profile</h5>              </div>              <div class="card-body">                <form method="post" action="<?php echo site_url('admin/account/update/'.$user_id); ?>" >                  <div class="row">                    <div class="col-md-12 pl-3">                      <div class="form-group">                        <label for="exampleInputEmail1">Name</label>                        <input type="text"  value="<?php echo $account['name']; ?>" name="name" class="form-control">                      </div>                    </div>                  </div>                  <div class="row">                    <div class="col-md-12 pr-1">                      <div class="form-group">                        <label>Email</label>                        <input type="email" value="<?php echo $account['email']; ?>" name="email" class="form-control">                      </div>                    </div>                	</div>                  	<!-- <div class="row">						<div class="col-md-12 text-center">							<button class="flashy__success">Success</button>						</div>					</div> -->                </form>                 <div class="content">          			<div class="row">          				<div class="col-md-2"></div>          				<div class="col-md-8">          					<div class="card">               					<h5 class="title" style="text-align: center; margin-top: 20px;">Login form</h5>         			 			<form action="<?php echo site_url('admin/change_pass'); ?>" method="post" style="padding: 20px;" >         			 				<div class="row">         			 					<div class="col-md-12">				                      		<div class="form-group">						                        <label>Current password</label>						                        <input type="password" name='current_pass' class="form-control" required>					                      	</div>                    					</div>                    					<div class="col-md-12">				                      		<div class="form-group">						                        <label>New password</label>						                        <input id='new_pass' type="password" class="form-control" required>					                      	</div>                    					</div>                    					<div class="col-md-12">				                      		<div class="form-group">						                        <label>Confirm new password</label>						                        <input name="new_pass" id="confirm_new_pass" type="password" class="form-control" required>					                      	</div>                    					</div>                    												<div class="col-md-12">											<div class="form-group text-center">												<input id="pass_button" type="submit" class="btn btn-primary w-50" name="" value="Submit">											</div>										</div>										         			 				</div>         			 				         			 			</form>	             			 		</div>          					</div>	          				</div>          			</div>	          		</div>              </div>            </div>          </div><script>	$('document').ready(function(){		$('#new_pass').keyup(function(){			var new_pass = $('#new_pass').val();			var confirm_new_pass = $('#confirm_new_pass').val();			if(new_pass != '' && confirm_new_pass != ''){				if(new_pass != confirm_new_pass){					$('#new_pass').css({"border-color" : "red", "border-width" : "1px", "border-style" : "solid"});					$('#confirm_new_pass').css({"border-color" : "red", "border-width" : "1px", "border-style" : "solid"});					$('#pass_button').attr('disabled', true);				}else{					$('#new_pass').css({"border-color" : "green", "border-width" : "1px", "border-style" : "solid"});					$('#confirm_new_pass').css({"border-color" : "green", "border-width" : "1px", "border-style" : "solid"});					$('#pass_button').attr('disabled', false);				}			}		});		$('#confirm_new_pass').keyup(function(){			var new_pass = $('#new_pass').val();			var confirm_new_pass = $('#confirm_new_pass').val();			if(new_pass != '' && confirm_new_pass != ''){				if(new_pass != confirm_new_pass){					$('#new_pass').css({"border-color" : "red", "border-width" : "1px", "border-style" : "solid"});					$('#confirm_new_pass').css({"border-color" : "red", "border-width" : "1px", "border-style" : "solid"});					$('#pass_button').attr('disabled', true);				}else{					$('#new_pass').css({"border-color" : "green", "border-width" : "1px", "border-style" : "solid"});					$('#confirm_new_pass').css({"border-color" : "green", "border-width" : "1px", "border-style" : "solid"});					$('#pass_button').attr('disabled', false);				}			}		});		$('#confirm_new_pass').blur(function(){			$('#new_pass').css({"border-color" : "#e3e3e3", "border-width" : "1px", "border-style" : "solid"});			$('#confirm_new_pass').css({"border-color" : "#e3e3e3", "border-width" : "1px", "border-style" : "solid"});		});		$('#new_pass').blur(function(){			$('#new_pass').css({"border-color" : "#e3e3e3", "border-width" : "1px", "border-style" : "solid"});			$('#confirm_new_pass').css({"border-color" : "#e3e3e3", "border-width" : "1px", "border-style" : "solid"});		});	});</script>